import React, { Fragment } from "react";
import ReactDOM from "react-dom";
import Button from './Button'
import './styles.css'

function soma(a, b) {
  alert(a + b);
}

const App = () => {
  return (
    <div className="App">Hello Word! <Button onClick={() => soma(1)} /></div> // componentes são common case, ou seja sempre maiuscula
  )
}

const rootElement = document.getElementById('root')
ReactDOM.render(<App />, rootElement)
